import React, { useState } from 'react';
import TodoForm from './TodoForm';
import Todo from './Todo'; // Import the updated Todo component
import './app.css';

function TodoApp() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');

  const addTask = (text) => {
    if (text) {
      setTasks([...tasks, { id: Date.now(), text, isComplete: false }]);
      setNewTask('');
    }
  };

  const removeTask = (id) => {
    const updatedTasks = tasks.filter((task) => task.id !== id);
    setTasks(updatedTasks);
  };

  const completeTask = (id) => {
    const updatedTasks = tasks.map((task) =>
      task.id === id ? { ...task, isComplete: !task.isComplete } : task
    );
    setTasks(updatedTasks);
  };

  return (
    <div className='todo-app'>
      <h1>Todo App</h1>
      <TodoForm onSubmit={addTask} />
      <Todo
        todos={tasks}
        completeTodo={completeTask}
        removeTodo={removeTask}
        updateTodo={(id, newValue) => {
          setTasks((prevTasks) =>
            prevTasks.map((task) =>
              task.id === id ? { ...task, text: newValue } : task
            )
          );
        }}
      />
    </div>
  );
}

export default TodoApp;
